"use client";

import { useNavbarLogo } from "@/lib/utils";
import {
  useScroll,
  useTransform,
  motion,
  useMotionValueEvent,
} from "framer-motion";
import { useTheme } from "next-themes";
import { useEffect, useRef, useState } from "react";
import {
  AudixLogo,
  ETCLogo,
  GitHubIcon,
  HarmanLogo,
  NextJSLogo,
  OBSLogo,
  RadixUIIcon,
  ReactLogo,
  ShureLogo,
  SoundcraftHarmanLogo,
  TailwindCssIcon,
  VisualStudioCodeIcon,
} from "../ui/icons";
